const express = require('express');

const app = express();

// Utilisation des middlewares 
app.use((req, res) => {
    res.json({ message: 'Votre requête a bien été reçue !' }); 
 });

app.use((req, res, next) => {
  console.log('Requête reçue !');
  next();
});

app.use((req, res, next) => {
  res.status(201);
  next();
});

app.use((req, res, next) => {
  res.json({ message: 'Votre requête a bien été reçue !' });
  next();
});

app.use((req, res, next) => {
  console.log('Réponse envoyée avec succès !');
});

module.exports = app;